import React, { useEffect, useState } from "react";
import { getGeneros } from "../../services/api";
import { Link } from "react-router-dom";

const GenerosList = () => {
    const [generos, setGeneros] = useState([]);

    useEffect(() => {
        getGeneros().then(setGeneros);
    }, []);

    return (
        <div className="container">
            <div className="flex items-center justify-between mb-4">
                <h2 className="text-2xl font-bold text-yellow-400">Lista de Géneros</h2>
                <Link to="/generos/agregar" className="btn btn-primary">
                    + Agregar Género
                </Link>
            </div>
            <ul className="card">
                {generos.map((genero) => (
                    <li key={genero.id} className="border-b py-2 flex justify-between items-center">
                        <span>
                            {genero.nombre} - {genero.estado} ({genero.descripcion})
                        </span>
                        <Link to={`/generos/editar/${genero.id}`} className="btn btn-primary">
                            Actualizar
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default GenerosList;
