import React, { useState } from "react";
import { addDirector } from "../services/api";

const AddDirector = () => {
    const [nombres, setNombres] = useState("");
    const [estado, setEstado] = useState("Activo");
    const [error, setError] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(""); // Limpiar error previo
        try {
            const response = await addDirector({ nombres, estado });
            console.log("✅ Director agregado:", response);
            setNombres("");
            setEstado("Activo");
            alert("✅ Director agregado correctamente!");
        } catch (error) {
            console.error("❌ Error al agregar director:", error);
            setError("No se pudo agregar el director. Verifica la consola.");
        }
    };

    return (
        <div>
            <h2>Agregar Director</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    placeholder="Nombre" 
                    value={nombres} 
                    onChange={(e) => setNombres(e.target.value)} 
                    required 
                />
                <select value={estado} onChange={(e) => setEstado(e.target.value)} required>
                    <option value="Activo">Activo</option>
                    <option value="Inactivo">Inactivo</option>
                </select>
                <button type="submit">Agregar</button>
            </form>
        </div>
    );
};

export default AddDirector;
