import React, { useState, useEffect } from "react";
import { getPeliculas, addPelicula } from "../../services/api";
import "../../styles/PeliculasList.css";

const PeliculasList = () => {
    const [peliculas, setPeliculas] = useState([]);
    const [titulo, setTitulo] = useState("");
    const [anio_estreno, setAnioEstreno] = useState("");
    const [sinopsis, setSinopsis] = useState("");
    const [imagen, setImagen] = useState("");
    const [url, setUrl] = useState("");
    const [search, setSearch] = useState(""); // Estado para la búsqueda
    const [modoAgregar, setModoAgregar] = useState(false); // Estado para alternar entre agregar/buscar

    useEffect(() => {
        getPeliculas().then(setPeliculas);
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Se arma el objeto con todos los campos que espera el backend.
        const nuevaPelicula = {
            serial: `SERIAL-${Date.now()}`,
            titulo,
            sinopsis,
            url,
            imagen,
            anio_estreno: parseInt(anio_estreno, 10),
            genero_id: 1,      // Asigna el ID correspondiente o un valor por defecto
            director_id: 1,    // Asigna el ID correspondiente o un valor por defecto
            productora_id: 1,  // Asigna el ID correspondiente o un valor por defecto
            tipo_id: 1         // Asigna el ID correspondiente o un valor por defecto
        };

        try {
            await addPelicula(nuevaPelicula);
            alert("✅ Película agregada correctamente!");
            setTitulo("");
            setAnioEstreno("");
            setSinopsis("");
            setImagen("");
            setUrl("");
            getPeliculas().then(setPeliculas); // Recargar lista de películas
        } catch (error) {
            console.error("❌ Error al agregar película:", error);
            alert("❌ No se pudo agregar la película. Revisa la consola.");
        }
    };

    // Filtrar películas por búsqueda
    const peliculasFiltradas = peliculas.filter((pelicula) =>
        pelicula.titulo.toLowerCase().includes(search.toLowerCase())
    );

    return (
        <div className="peliculas-container">
            {/* Menú de navegación */}
            <nav className="menu">
                <button onClick={() => setModoAgregar(false)}>🔍 Buscar Películas</button>
                <button onClick={() => setModoAgregar(true)}>➕ Agregar Película</button>
            </nav>

            {modoAgregar ? (
                // Formulario para agregar película
                <div className="form-container">
                    <h2>Agregar Película</h2>
                    <form onSubmit={handleSubmit} className="pelicula-form">
                        <input 
                            type="text" 
                            placeholder="Título" 
                            value={titulo} 
                            onChange={(e) => setTitulo(e.target.value)} 
                            required 
                        />
                        <input 
                            type="number" 
                            placeholder="Año de estreno" 
                            value={anio_estreno} 
                            onChange={(e) => setAnioEstreno(e.target.value)} 
                            required 
                        />
                        <textarea 
                            placeholder="Sinopsis"
                            value={sinopsis}
                            onChange={(e) => setSinopsis(e.target.value)}
                        />
                        <input 
                            type="text" 
                            placeholder="URL de la imagen" 
                            value={imagen} 
                            onChange={(e) => setImagen(e.target.value)} 
                            required 
                        />
                        <input 
                            type="text" 
                            placeholder="Enlace de la película" 
                            value={url} 
                            onChange={(e) => setUrl(e.target.value)} 
                        />
                        <button type="submit">Agregar</button>
                    </form>
                </div>
            ) : (
                // Sección de búsqueda y lista de películas
                <div>
                    <h2>Buscar Películas</h2>
                    <input 
                        type="text" 
                        placeholder="Buscar por título..." 
                        value={search} 
                        onChange={(e) => setSearch(e.target.value)} 
                        className="search-bar"
                    />
                    <div className="peliculas-grid">
                        {peliculasFiltradas.length > 0 ? (
                            peliculasFiltradas.map((pelicula) => (
                                <div key={pelicula.id} className="pelicula-card">
                                    <img src={pelicula.imagen} alt={pelicula.titulo} className="pelicula-img"/>
                                    <div className="pelicula-info">
                                        <h3>{pelicula.titulo}</h3>
                                        <p><strong>Año:</strong> {pelicula.anio_estreno}</p>
                                        <p><strong>Sinopsis:</strong> {pelicula.sinopsis || "No disponible"}</p>
                                        <a href={pelicula.url} target="_blank" rel="noopener noreferrer" className="ver-mas-btn">Ver más</a>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p>⚠️ No se encontraron películas.</p>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default PeliculasList;
