import { useState, useEffect } from "react";
import { getGeneros, postPelicula } from "../api/api";

const AddPelicula = () => {
    const [titulo, setTitulo] = useState("");
    const [anio, setAnio] = useState("");
    const [generos, setGeneros] = useState([]);
    const [generoSeleccionado, setGeneroSeleccionado] = useState("");
    const [mensaje, setMensaje] = useState("");

    useEffect(() => {
        const cargarGeneros = async () => {
            try {
                const data = await getGeneros();
                const generosActivos = data.filter(g => g.estado.trim().toLowerCase() === "activo");
                setGeneros(generosActivos);
                setGeneroSeleccionado(prev => prev || (generosActivos.length > 0 ? generosActivos[0].id : ""));
            } catch (error) {
                console.error("Error al cargar los géneros:", error);
            }
        };
        cargarGeneros();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!titulo || !anio || !generoSeleccionado) {
            setMensaje("⚠️ Todos los campos son obligatorios.");
            return;
        }
        
        try {
            await postPelicula({ titulo, anio, id_genero: generoSeleccionado });
            setMensaje("✅ Película agregada con éxito.");
            setTitulo("");
            setAnio("");
        } catch (error) {
            console.error("Error al agregar película:", error);
            setMensaje("❌ Error al agregar la película.");
        }
    };

    return (
        <div>
            <h2>➕ Agregar Película</h2>
            {mensaje && <p>{mensaje}</p>}
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Título" value={titulo} onChange={(e) => setTitulo(e.target.value)} required />
                <input type="number" placeholder="Año" value={anio} onChange={(e) => setAnio(e.target.value)} required />
                <select value={generoSeleccionado} onChange={(e) => setGeneroSeleccionado(e.target.value)}>
                    {generos.map(genero => (
                        <option key={genero.id} value={genero.id}>{genero.nombre}</option>
                    ))}
                </select>
                <button type="submit">Agregar</button>
            </form>
        </div>
    );
};

export default AddPelicula;
