import React, { useState, useEffect } from "react";
import { addPelicula, getGeneros, getDirectores, getProductoras, getTipos } from "../../services/api";

const AddPelicula = () => {
    // Estados de los campos del formulario
    const [titulo, setTitulo] = useState("");
    const [sinopsis, setSinopsis] = useState("");
    const [imagen, setImagen] = useState("");
    const [url, setUrl] = useState("");
    const [anioEstreno, setAnioEstreno] = useState("");

    // Estados para los selects (datos de tablas relacionadas)
    const [generos, setGeneros] = useState([]);
    const [directores, setDirectores] = useState([]);
    const [productoras, setProductoras] = useState([]);
    const [tipos, setTipos] = useState([]);

    // Estados para almacenar la selección actual
    const [generoSeleccionado, setGeneroSeleccionado] = useState("");
    const [directorSeleccionado, setDirectorSeleccionado] = useState("");
    const [productoraSeleccionada, setProductoraSeleccionada] = useState("");
    const [tipoSeleccionado, setTipoSeleccionado] = useState("");

    // Cargar datos de las tablas relacionadas al iniciar
    useEffect(() => {
        // Filtrar solo géneros activos
        getGeneros().then(data => {
            const activeGeneros = data.filter(item => item.estado.toLowerCase() === "activo");
            setGeneros(activeGeneros);
            if (activeGeneros.length > 0) setGeneroSeleccionado(activeGeneros[0].id);
        });

        getDirectores().then(data => {
            const activeDirectores = data.filter(item => item.estado.toLowerCase() === "activo");
            setDirectores(activeDirectores);
            if (activeDirectores.length > 0) setDirectorSeleccionado(activeDirectores[0].id);
        });
        getProductoras().then(data => {
            const activeProductoras = data.filter(item => item.estado === "Activo");
            setProductoras(activeProductoras);
            if (activeProductoras.length > 0) setProductoraSeleccionada(activeProductoras[0].id);
        });
        getTipos().then(data => {
            const activeTipos = data.filter(item => item.estado === "Activo");
            setTipos(activeTipos);
            if (activeTipos.length > 0) setTipoSeleccionado(activeTipos[0].id);
        });
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validación simple
        if (!titulo || !url || !imagen || !anioEstreno) {
            alert("Todos los campos obligatorios deben estar llenos.");
            return;
        }

        // Se arma el objeto con todos los campos requeridos por el modelo
        const nuevaPelicula = {
            serial: `SERIAL-${Date.now()}`,
            titulo,
            sinopsis,
            url,
            imagen,
            anio_estreno: parseInt(anioEstreno, 10),
            genero_id: parseInt(generoSeleccionado, 10),
            director_id: parseInt(directorSeleccionado, 10),
            productora_id: parseInt(productoraSeleccionada, 10),
            tipo_id: parseInt(tipoSeleccionado, 10)
        };

        try {
            await addPelicula(nuevaPelicula);
            alert("✅ Película agregada!");
            // Limpiar campos
            setTitulo("");
            setSinopsis("");
            setImagen("");
            setUrl("");
            setAnioEstreno("");
        } catch (error) {
            console.error("❌ Error al agregar película:", error);
            alert("❌ No se pudo agregar la película. Revisa la consola.");
        }
    };

    return (
        <div>
            <h2>Agregar Película</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Título"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Sinopsis"
                    value={sinopsis}
                    onChange={(e) => setSinopsis(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="URL de la imagen"
                    value={imagen}
                    onChange={(e) => setImagen(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Enlace de la película"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    required
                />
                <input
                    type="number"
                    placeholder="Año de estreno"
                    value={anioEstreno}
                    onChange={(e) => setAnioEstreno(e.target.value)}
                    required
                />

                {/* Select para Género (solo activos) */}
                <select
                    value={generoSeleccionado}
                    onChange={(e) => setGeneroSeleccionado(e.target.value)}
                    required
                >
                    {generos.map((genero) => (
                        <option key={genero.id} value={genero.id}>
                            {genero.nombre}
                        </option>
                    ))}
                </select>

                {/* Select para Director */}
                <select
                    value={directorSeleccionado}
                    onChange={(e) => setDirectorSeleccionado(e.target.value)}
                    required
                >
                    {directores.length > 0 ? (
                        directores.map((director) => (
                            <option key={director.id} value={director.id}>
                                {director.nombre || "Sin nombre"}
                            </option>
                        ))
                    ) : (
                        <option value="">No hay directores activos</option>
                    )}
                </select>


                {/* Select para Productora */}
                <select
                    value={productoraSeleccionada}
                    onChange={(e) => setProductoraSeleccionada(e.target.value)}
                    required
                >
                    {productoras.map((prod) => (
                        <option key={prod.id} value={prod.id}>
                            {prod.nombre}
                        </option>
                    ))}
                </select>

                {/* Select para Tipo */}
                <select
                    value={tipoSeleccionado}
                    onChange={(e) => setTipoSeleccionado(e.target.value)}
                    required
                >
                    {tipos.map((tipo) => (
                        <option key={tipo.id} value={tipo.id}>
                            {tipo.nombre}
                        </option>
                    ))}
                </select>

                <button type="submit">Agregar</button>
            </form>
        </div>
    );
};

export default AddPelicula;
