import React, { useState, useEffect } from "react";
import { getPeliculas, addPelicula } from "../../services/api";
import "../../styles/PeliculasList.css";

const PeliculasList = () => {
    const [peliculas, setPeliculas] = useState([]);
    const [titulo, setTitulo] = useState("");
    const [anio_estreno, setAnioEstreno] = useState("");
    const [sinopsis, setSinopsis] = useState("");
    const [imagen, setImagen] = useState("");
    const [url, setUrl] = useState("");
    const [search, setSearch] = useState(""); // Estado para la b√∫squeda
    const [modoAgregar, setModoAgregar] = useState(false); // Estado para alternar entre agregar/buscar

    useEffect(() => {
        getPeliculas().then(setPeliculas);
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await addPelicula({ titulo, anio_estreno, sinopsis, imagen, url });
            setTitulo("");
            setAnioEstreno("");
            setSinopsis("");
            setImagen("");
            setUrl("");
            alert("‚úÖ Pel√≠cula agregada correctamente!");
            getPeliculas().then(setPeliculas); // Recargar lista de pel√≠culas
        } catch (error) {
            console.error("‚ùå Error al agregar pel√≠cula:", error);
            alert("‚ùå No se pudo agregar la pel√≠cula. Revisa la consola.");
        }
    };

    // Filtrar pel√≠culas por b√∫squeda
    const peliculasFiltradas = peliculas.filter((pelicula) =>
        pelicula.titulo.toLowerCase().includes(search.toLowerCase())
    );

    return (
        <div className="peliculas-container">
            {/* Men√∫ de navegaci√≥n */}
            <nav className="menu">
                <button onClick={() => setModoAgregar(false)}>üîç Buscar Pel√≠culas</button>
                <button onClick={() => setModoAgregar(true)}>‚ûï Agregar Pel√≠cula</button>
            </nav>

            {modoAgregar ? (
                // Formulario para agregar pel√≠cula
                <div className="form-container">
                    <h2>Agregar Pel√≠cula</h2>
                    <form onSubmit={handleSubmit} className="pelicula-form">
                        <input 
                            type="text" 
                            placeholder="T√≠tulo" 
                            value={titulo} 
                            onChange={(e) => setTitulo(e.target.value)} 
                            required 
                        />
                        <input 
                            type="number" 
                            placeholder="A√±o de estreno" 
                            value={anio_estreno} 
                            onChange={(e) => setAnioEstreno(e.target.value)} 
                            required 
                        />
                        <textarea 
                            placeholder="Sinopsis"
                            value={sinopsis}
                            onChange={(e) => setSinopsis(e.target.value)}
                        />
                        <input 
                            type="text" 
                            placeholder="URL de la imagen" 
                            value={imagen} 
                            onChange={(e) => setImagen(e.target.value)} 
                            required 
                        />
                        <input 
                            type="text" 
                            placeholder="Enlace de la pel√≠cula" 
                            value={url} 
                            onChange={(e) => setUrl(e.target.value)} 
                        />
                        <button type="submit">Agregar</button>
                    </form>
                </div>
            ) : (
                // Secci√≥n de b√∫squeda y lista de pel√≠culas
                <div>
                    <h2>Buscar Pel√≠culas</h2>
                    <input 
                        type="text" 
                        placeholder="Buscar por t√≠tulo..." 
                        value={search} 
                        onChange={(e) => setSearch(e.target.value)} 
                        className="search-bar"
                    />
                    <div className="peliculas-grid">
                        {peliculasFiltradas.length > 0 ? (
                            peliculasFiltradas.map((pelicula) => (
                                <div key={pelicula.id} className="pelicula-card">
                                    <img src={pelicula.imagen} alt={pelicula.titulo} className="pelicula-img"/>
                                    <div className="pelicula-info">
                                        <h3>{pelicula.titulo}</h3>
                                        <p><strong>A√±o:</strong> {pelicula.anio_estreno}</p>
                                        <p><strong>Sinopsis:</strong> {pelicula.sinopsis || "No disponible"}</p>
                                        <a href={pelicula.url} target="_blank" rel="noopener noreferrer" className="ver-mas-btn">Ver m√°s</a>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <p>‚ö†Ô∏è No se encontraron pel√≠culas.</p>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default PeliculasList;
