import { useState } from "react";

const AddPelicula = () => {
    const [titulo, setTitulo] = useState("");
    const [director, setDirector] = useState("");
    const [anio, setAnio] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validaciones antes de enviar la data
        if (!titulo || !director || !anio) {
            alert("Todos los campos son obligatorios.");
            return;
        }

        if (isNaN(anio) || anio < 1888) {
            alert("Ingrese un año válido.");
            return;
        }

        const nuevaPelicula = { titulo, director, anio };

        try {
            const response = await fetch("http://localhost:3000/medias", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(nuevaPelicula),
            });

            if (!response.ok) {
                throw new Error("Error al agregar la película");
            }

            alert("Película agregada exitosamente");
            setTitulo("");
            setDirector("");
            setAnio("");
        } catch (error) {
            console.error("Error:", error);
            alert("No se pudo agregar la película. Intente de nuevo.");
        }
    };

    return (
        <div>
            <h2>Agregar Película</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Título"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Director"
                    value={director}
                    onChange={(e) => setDirector(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Año"
                    value={anio}
                    onChange={(e) => setAnio(e.target.value)}
                />
                <button type="submit">Agregar</button>
            </form>
        </div>
    );
};

export default AddPelicula;
