import React, { useEffect, useState } from "react";
import { addPelicula } from "../../services/api";

const AddPelicula = () => {
    const [titulo, setTitulo] = useState("");
    const [director, setDirector] = useState("");
    const [anio, setAnio] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        await addPelicula({ titulo, director, anio });
        setTitulo("");
        setDirector("");
        setAnio("");
        alert("Película agregada!");
    };

    return (
        <div>
            <h2>Agregar Película</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Título" value={titulo} onChange={(e) => setTitulo(e.target.value)} required />
                <input type="text" placeholder="Director" value={director} onChange={(e) => setDirector(e.target.value)} required />
                <input type="number" placeholder="Año" value={anio} onChange={(e) => setAnio(e.target.value)} required />
                <button type="submit">Agregar</button>
            </form>
        </div>
    );
    console.log("Datos enviados:", pelicula);

};

export default AddPelicula;
