import React, { useState, useEffect } from "react";
import { getPeliculas, addPelicula } from "../../services/api";
import "../../styles/PeliculasList.css";

const PeliculasList = () => {
    const [peliculas, setPeliculas] = useState([]);
    const [titulo, setTitulo] = useState("");
    const [anio_estreno, setAnioEstreno] = useState("");
    const [sinopsis, setSinopsis] = useState("");
    const [imagen, setImagen] = useState("");
    const [url, setUrl] = useState("");

    useEffect(() => {
        getPeliculas().then(setPeliculas);
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await addPelicula({ titulo, anio_estreno, sinopsis, imagen, url });
            setTitulo("");
            setAnioEstreno("");
            setSinopsis("");
            setImagen("");
            setUrl("");
            alert("✅ Película agregada correctamente!");
            getPeliculas().then(setPeliculas); // Recargar lista de películas
        } catch (error) {
            console.error("❌ Error al agregar película:", error);
            alert("❌ No se pudo agregar la película. Revisa la consola.");
        }
    };

    return (
        <div className="peliculas-container">
            <h2>Lista de Películas</h2>

            {/* Formulario para agregar nueva película */}
            <form onSubmit={handleSubmit} className="pelicula-form">
                <input 
                    type="text" 
                    placeholder="Título" 
                    value={titulo} 
                    onChange={(e) => setTitulo(e.target.value)} 
                    required 
                />
                <input 
                    type="number" 
                    placeholder="Año de estreno" 
                    value={anio_estreno} 
                    onChange={(e) => setAnioEstreno(e.target.value)} 
                    required 
                />
                <textarea 
                    placeholder="Sinopsis"
                    value={sinopsis}
                    onChange={(e) => setSinopsis(e.target.value)}
                />
                <input 
                    type="text" 
                    placeholder="URL de la imagen" 
                    value={imagen} 
                    onChange={(e) => setImagen(e.target.value)} 
                    required 
                />
                <input 
                    type="text" 
                    placeholder="Enlace de la película" 
                    value={url} 
                    onChange={(e) => setUrl(e.target.value)} 
                />
                <button type="submit">Agregar Película</button>
            </form>

            {/* Lista de películas */}
            <div className="peliculas-grid">
                {peliculas.map((pelicula) => (
                    <div key={pelicula.id} className="pelicula-card">
                        <img src={pelicula.imagen} alt={pelicula.titulo} className="pelicula-img"/>
                        <div className="pelicula-info">
                            <h3>{pelicula.titulo}</h3>
                            <p><strong>Año:</strong> {pelicula.anio_estreno}</p>
                            <p><strong>Sinopsis:</strong> {pelicula.sinopsis || "No disponible"}</p>
                            <a href={pelicula.url} target="_blank" rel="noopener noreferrer" className="ver-mas-btn">Ver más</a>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default PeliculasList;
