import React, { useState } from "react";
import { addDirector } from "../services/api";

const AddDirector = () => {
    const [nombre, setNombre] = useState("");
    const [estado, setEstado] = useState("");
    const [descripcion, setDescripcion] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        await addDirector({ nombre, estado, descripcion });
        setNombre("");
        setEstado("");
        setDescripcion("");
        alert("Director agregado!");
    };

    return (
        <div>
            <h2>Agregar Director</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Nombre" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
                <input type="text" placeholder="Estado" value={estado} onChange={(e) => setEstado(e.target.value)} required />
                <input type="text" placeholder="DescripciÃ³n" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} required />
                <button type="submit">Agregar</button>
            </form>
        </div>
    );
};

export default AddDirector;
