import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getGeneroById, updateGenero } from "../../services/api";

const UpdateGenero = () => {
    const { id } = useParams(); // Obtener el ID desde la URL
    const navigate = useNavigate();
    
    const [genero, setGenero] = useState({
        nombre: "",
        estado: "",
        descripcion: ""
    });

    useEffect(() => {
        console.log("ID recibido:", id); // Depuración

        if (!id) {
            console.error("Error: No se recibió un ID válido.");
            return;
        }

        getGeneroById(id)
            .then((data) => {
                if (!data) {
                    console.error("Error: No se encontró el género.");
                    return;
                }
                setGenero({
                    nombre: data.nombre || "",
                    estado: data.estado || "",
                    descripcion: data.descripcion || ""
                });
            })
            .catch((error) => console.error("Error obteniendo género:", error));
    }, [id]);

    const handleChange = (e) => {
        setGenero({ ...genero, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateGenero(id, genero);
            alert("✅ Género actualizado correctamente!");
            navigate("/generos"); // Redirigir a la lista de géneros
        } catch (error) {
            console.error("❌ Error al actualizar género:", error);
            alert("❌ No se pudo actualizar el género. Revisa la consola.");
        }
    };

    if (!id) {
        return <p className="text-red-500">Error: No se encontró el ID del género.</p>;
    }

    return (
        <div className="p-6">
            <h2 className="text-2xl font-bold mb-4">Actualizar Género</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium">Nombre:</label>
                    <input 
                        type="text" 
                        name="nombre" 
                        value={genero.nombre} 
                        onChange={handleChange} 
                        className="border rounded w-full p-2"
                        required 
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium">Estado:</label>
                    <select 
                        name="estado" 
                        value={genero.estado} 
                        onChange={handleChange} 
                        className="border rounded w-full p-2"
                        required
                    >
                        <option value="Activo">Activo</option>
                        <option value="Inactivo">Inactivo</option>
                    </select>
                </div>
                <div>
                    <label className="block text-sm font-medium">Descripción:</label>
                    <textarea 
                        name="descripcion" 
                        value={genero.descripcion} 
                        onChange={handleChange} 
                        className="border rounded w-full p-2"
                    />
                </div>
                <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-700">
                    Actualizar Género
                </button>
            </form>
        </div>
    );
};

export default UpdateGenero;
