import React, { useState } from "react";
import { addPelicula } from "../../services/api";

const AddPelicula = () => {
    const [titulo, setTitulo] = useState("");
    const [director, setDirector] = useState("");
    const [anio, setAnio] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Se genera un objeto completo con los campos requeridos.
        const nuevaPelicula = {
            serial: `SERIAL-${Date.now()}`,
            titulo,
            // Se asume que el campo "director" enviado aquí corresponde al nombre, 
            // pero el backend espera un ID. Se asigna director_id por defecto.
            director: director,
            anio: parseInt(anio, 10),
            sinopsis: "", // Valor por defecto, ya que no se ingresa en este formulario
            imagen: "",   // Valor por defecto
            url: "",      // Valor por defecto
            genero_id: 1, // Valor fijo o se puede modificar según tus necesidades
            director_id: 1, // Valor fijo (reemplaza por el ID correspondiente si es necesario)
            productora_id: 1, // Valor fijo
            tipo_id: 1       // Valor fijo
        };

        try {
            await addPelicula(nuevaPelicula);
            setTitulo("");
            setDirector("");
            setAnio("");
            alert("Película agregada!");
        } catch (error) {
            console.error("❌ Error al agregar película:", error);
            alert("❌ No se pudo agregar la película. Revisa la consola.");
        }
    };

    return (
        <div>
            <h2>Agregar Película</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Título"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Director"
                    value={director}
                    onChange={(e) => setDirector(e.target.value)}
                    required
                />
                <input
                    type="number"
                    placeholder="Año"
                    value={anio}
                    onChange={(e) => setAnio(e.target.value)}
                    required
                />
                <button type="submit">Agregar</button>
            </form>
        </div>
    );
};

export default AddPelicula;
