import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { getGeneroById, updateGenero } from "../../services/api";

const UpdateGenero = () => {
    const { id } = useParams(); // Obtener el ID desde la URL
    const navigate = useNavigate();
    
    const [genero, setGenero] = useState({
        nombre: "",
        estado: "",
        descripcion: ""
    });

    useEffect(() => {
        getGeneroById(id).then((data) => {
            setGenero({
                nombre: data.nombre,
                estado: data.estado,
                descripcion: data.descripcion
            });
        });
    }, [id]);

    const handleChange = (e) => {
        setGenero({ ...genero, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await updateGenero(id, genero);
        navigate("/generos"); // Redirigir a la lista de géneros
    };

    return (
        <div className="p-6">
            <h2 className="text-2xl font-bold mb-4">Actualizar Género</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium">Nombre:</label>
                    <input 
                        type="text" 
                        name="nombre" 
                        value={genero.nombre} 
                        onChange={handleChange} 
                        className="border rounded w-full p-2"
                        required 
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium">Estado:</label>
                    <select 
                        name="estado" 
                        value={genero.estado} 
                        onChange={handleChange} 
                        className="border rounded w-full p-2"
                        required
                    >
                        <option value="Activo">Activo</option>
                        <option value="Inactivo">Inactivo</option>
                    </select>
                </div>
                <div>
                    <label className="block text-sm font-medium">Descripción:</label>
                    <textarea 
                        name="descripcion" 
                        value={genero.descripcion} 
                        onChange={handleChange} 
                        className="border rounded w-full p-2"
                    />
                </div>
                <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-700">
                    Actualizar Género
                </button>
            </form>
        </div>
    );
};

export default UpdateGenero;
