import { useEffect, useState } from "react";
import { getPeliculas, getDirectores } from "../services/api";

export default function Dashboard() {
  const [peliculas, setPeliculas] = useState([]);
  const [directores, setDirectores] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const peliculasData = await getPeliculas();
      const directoresData = await getDirectores();
      setPeliculas(peliculasData);
      setDirectores(directoresData);
    }
    fetchData();
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Dashboard</h2>
      <div className="grid grid-cols-2 gap-4">
        <div className="bg-blue-500 text-white p-4 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold">Pel√≠culas Registradas</h3>
          <p className="text-4xl">{peliculas.length}</p>
        </div>
        <div className="bg-green-500 text-white p-4 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold">Directores Registrados</h3>
          <p className="text-4xl">{directores.length}</p>
        </div>
      </div>
    </div>
  );
}
